INSERT INTO `tbl_product` VALUES (21, '', 'Tiểu cảnh nước trong nhà phố', '36104-m.jpg', '36104-m.jpg', '', 2010, 2010, 0, 0, '', '<br />\r\n<table class="contentpaneopen">\r\n    <tbody>\r\n        <tr>\r\n            <td valign="top">\r\n            <div>\r\n            <div style="text-align: center;"><span style="font-size: medium;"><img width="350" height="251" src="http://sonthuy.vn/images/stories/tieu-canh/tieucanhnuoc002.jpg" title="" alt="" /></span>   </div>\r\n            <p style="text-align: center;" class="Lead"><span style="font-size: medium;">Theo  phong c&aacute;ch ch&acirc;u &Aacute;, trang tr&iacute; tiểu cảnh nước trong s&acirc;n vườn c&oacute; nhiều điểm kh&aacute;c với ch&acirc;u &Acirc;u. Người ch&acirc;u &Aacute; n&oacute;i chung rất trọng phong thủy n&ecirc;n lu&ocirc;n tu&acirc;n theo những quy tắc nhất định.</span></p>\r\n            <table cellspacing="0" cellpadding="3" border="0" align="center" style="width: 1px;">\r\n                <tbody>\r\n                    <tr>\r\n                        <td style="text-align: center;"><img width="300" height="200" alt="" style="border: 1px solid black;" src="http://sonthuy.vn/images/stories/tieu-canh-01/tc1.jpg" title="" /></td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td class="Image" style="text-align: center;">Tạo d&ograve;ng chảy bằng những con giống, b&igrave;nh gốm.</td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n            <p style="text-align: center;" class="Normal"><span style="font-size: medium;">Người  ch&acirc;u &Aacute; thường trang tr&iacute; mặt nước với phong c&aacute;ch nước tĩnh thay v&igrave; động như ch&acirc;u &Acirc;u. Họ cũng cầu kỳ hơn, thể hiện r&otilde; ở việc trang tr&iacute; theo dạng non bộ. Non bộ m&ocirc; phỏng lại m&ocirc; h&igrave;nh tự nhi&ecirc;n với non nước, c&acirc;y cối, c&ocirc;ng  tr&igrave;nh kiến tr&uacute;c, cảnh sinh hoạt...</span></p>\r\n            <table cellspacing="0" cellpadding="3" border="0" align="center" style="width: 396px;">\r\n                <tbody>\r\n                    <tr>\r\n                        <td style="text-align: center;"><img width="190" height="150" alt="" style="border: 1px solid black;" src="http://sonthuy.vn/images/stories/tieu-canh-01/tc5.jpg" title="" /></td>\r\n                        <td style="text-align: center;"><img width="190" height="150" alt="" style="border: 1px solid black;" src="http://sonthuy.vn/images/stories/tieu-canh-01/tc2.jpg" title="" /></td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td class="Image" style="text-align: center;">Non bộ được m&ocirc; phỏng cảnh sinh hoạt rất sinh động.</td>\r\n                        <td class="Image" style="text-align: center;">C&acirc;y thế, sinh vật cảnh cũng được đưa v&agrave;o tiểu cảnh nước.</td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n            <p style="text-align: center;" class="Normal"><span style="font-size: medium;">Phong  c&aacute;ch trang tr&iacute; tiểu cảnh nước, non bộ của người ch&acirc;u &Aacute; bị ảnh hưởng nhiều bởi phong thủy. V&igrave; vậy nếu trang tr&iacute; vườn theo phong c&aacute;ch ch&acirc;u &Aacute;, bạn kh&ocirc;ng thể l&agrave;m bừa. Nếu l&agrave;m theo dạng non bộ, c&acirc;y thế được sử dụng nhiều. Để tạo ra kh&ocirc;ng gian n&uacute;i non, th&aacute;c, suối giống thật, việc chọn lựa c&acirc;y cối, sinh vật cảnh cũng rất quan trọng.</span></p>\r\n            <table cellspacing="0" cellpadding="3" border="0" align="center" style="width: 320px;">\r\n                <tbody>\r\n                    <tr>\r\n                        <td style="text-align: center;"><img width="190" height="150" alt="" style="border: 1px solid black;" src="http://sonthuy.vn/images/stories/tieu-canh-01/tc8.jpg" title="" /></td>\r\n                        <td style="text-align: center;"><img width="190" height="150" alt="" style="border: 1px solid black;" src="http://sonthuy.vn/images/stories/tieu-canh-01/tc9.jpg" title="" /></td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td style="text-align: center;"><img width="190" height="150" alt="" src="http://sonthuy.vn/images/stories/tieu-canh-01/tc10.jpg" style="border: 1px solid black;" /></td>\r\n                        <td style="text-align: center;"><img width="190" height="150" alt="" src="http://sonthuy.vn/images/stories/tieu-canh-01/tc7.jpg" style="border: 1px solid black;" /></td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td colspan="2" class="Image" style="text-align: center;">Những sản phẩm b&aacute;n sẵn đưa nước v&agrave;o vườn.</td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n            <p style="text-align: center;" class="Normal"><span style="font-size: medium;">Bạn  n&ecirc;n chọn c&acirc;y nhỏ hoặc vừa phải để trang tr&iacute; v&igrave; nếu c&acirc;y to sẽ l&agrave;m cho n&uacute;i của bạn biến th&agrave;nh h&ograve;n đ&aacute;. C&aacute; trong suối cũng n&ecirc;n chọn những loại b&eacute;  để d&ograve;ng suối c&oacute; cảm gi&aacute;c rộng hơn. Nếu c&oacute; trang tr&iacute; th&ecirc;m c&aacute;c m&ocirc; h&igrave;nh bằng sứ như nh&agrave; cửa, động vật, người v&agrave;o th&igrave; n&ecirc;n ch&uacute; &yacute; để ch&uacute;ng tỷ lệ với nhau.</span></p>\r\n            <table cellspacing="0" cellpadding="3" border="0" align="center" style="width: 346px;">\r\n                <tbody>\r\n                    <tr>\r\n                        <td style="text-align: center;"><img alt="" src="http://sonthuy.vn/images/stories/tieu-canh-01/tc4.jpg" style="border: 1px solid black;" /></td>\r\n                        <td style="text-align: center;"><img alt="" src="http://sonthuy.vn/images/stories/tieu-canh-01/tc3.jpg" style="border: 1px solid black;" /></td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td colspan="2" class="Image" style="text-align: center;">Kết hợp đ&aacute;, nước, c&acirc;y thế, sinh vật cảnh.</td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n            <p style="text-align: center;" class="Normal"><span style="font-size: medium;">Với  kh&ocirc;ng gian vườn nhỏ, nếu kh&ocirc;ng c&oacute; điều kiện trang tr&iacute; non bộ, suối, th&aacute;c th&igrave; bạn vẫn c&oacute; thể đưa nước v&agrave;o khu vườn nhỏ bằng những sản phẩm l&agrave;m sẵn. C&oacute; thể l&agrave; th&aacute;c nước nhỏ được chế t&aacute;c sẵn chỉ việc đem về bầy, những v&ograve;i nước nhỏ chảy ra từ ống tre hay những bể nước được đục thủ c&ocirc;ng bằng đ&aacute; nguy&ecirc;n khối.</span></p>\r\n            </div>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>', 132, 0, 0, '', NULL, NULL, '0');
