INSERT INTO `tbl_product` VALUES (23, '', 'Cảnh quan sân vườn phong cách hiện đại', '36104-m.jpg', '36104-m.jpg', '', 2010, 2010, 0, 0, '', '<table class="contentpaneopen">\r\n    <tbody>\r\n        <tr>\r\n            <td valign="top">\r\n            <div style="text-align: left;">Sau đ&acirc;y l&agrave; một số mẫu thiết kế cảnh quan s&acirc;n vườn hiện đại m&agrave; qu&yacute; quị c&oacute; thể tham khảo.</div>\r\n            <div style="text-align: left;"><img width="660" height="440" src="http://sonthuy.vn/images/stories/canh-quan-san-vuon/landscape01.jpg" title="Cảnh quan s&acirc;n vườn hiện đại" alt="Cảnh quan s&acirc;n vườn hiện đại" />&nbsp;</div>\r\n            <div style="text-align: left;">&nbsp;</div>\r\n            <div style="text-align: left;"><img width="660" height="371" src="http://sonthuy.vn/images/stories/canh-quan-san-vuon/landscape02.jpg" title="San vuon phong cach hien dai" alt="San vuon phong cach hien dai" />&nbsp;</div>\r\n            <div style="text-align: left;"><img width="660" height="456" src="http://sonthuy.vn/images/stories/canh-quan-san-vuon/landscape03.jpg" title="Canh quan san vuon dep hien dai" alt="Canh quan san vuon dep hien dai" /></div>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p style="text-align: left;">&nbsp;</p>', 133, 0, 0, '', NULL, NULL, '0');
INSERT INTO `tbl_product` VALUES (24, '', 'Nguyên tắc thiết kế cảnh quan sân vườn.', '36104-m.jpg', '36104-m.jpg', '', 2010, 2010, 0, 0, '', '<table class="contentpaneopen">\r\n    <tbody>\r\n        <tr>\r\n            <td valign="top">\r\n            <p style="text-align: center;"><img width="400" height="295" src="http://sonthuy.vn/images/stories/canh-quan-san-vuon/landscape-design-photo-pool.jpg" title="" alt="" /></p>\r\n            <div align="justify">Thiết kế s&acirc;n vườn kh&ocirc;ng phải l&agrave; một m&ocirc;n khoa học ch&iacute;nh x&aacute;c v&agrave; những nguy&ecirc;n tắc được sử dụng để thiết kế cũng c&oacute; nhiều c&aacute;ch n&oacute;i kh&aacute;c nhau. Ba  phạm tr&ugrave; dưới đ&acirc;y chứa những yếu tố cơ bản m&agrave; khi kết hợp ch&uacute;ng với nhau sẽ đem lại những c&aacute;ch giải th&iacute;ch thường dễ chấp nhận cho việc thiết  kế cảnh quan s&acirc;n vườn đẹp. H&atilde;y nhớ rằng thiết kế cảnh quan s&acirc;n vườn l&agrave; việc d&agrave;nh cho c&aacute; nh&acirc;n v&agrave; những nguy&ecirc;n tắc của ch&uacute;ng c&oacute; thể được ph&aacute; bỏ.   <br />\r\n            <strong>Thứ tự &ndash; C&acirc;n bằng &ndash; Sự c&acirc;n đối</strong><br />\r\n            Cấu tr&uacute;c cơ bản của s&acirc;n vườn. Thứ tự c&oacute; thể được sữ dụng qua t&iacute;nh đối xứng, như trong một s&acirc;n vườn th&ocirc;ng thường, th&ocirc;ng qua việc d&ugrave;ng c&aacute;c loại c&acirc;y hay m&agrave;u sắc&hellip;<br />\r\n            <strong>H&agrave;i ho&agrave; hay Đồng nhất</strong><br />\r\n            Khi những phần của khu vườn c&oacute; t&aacute;c động chung với nhau như to&agrave;n bộ khu vườn. Điều n&agrave;y c&oacute; thể thực hiện bằng việc sử dụng giới hạn m&agrave;u sắc, c&aacute;c loại c&acirc;y&hellip; Chủ đề của khu vườn n&ecirc;n x&acirc;y cho đồng nhất.<br />\r\n            <strong>D&ograve;ng chảy, chuyển đổi hay nhịp điệu.</strong><br />\r\n            Li&ecirc;n tục giữ &aacute;nh mắt của bạn di chuyển v&agrave; trực tiếp v&agrave;o nơi bạn muốn để nh&igrave;n thấy sự chuyển đổi dần từ độ cao v&agrave; m&agrave;u sắc để tr&aacute;nh tầm nh&igrave;n của bạn bị dừng lại đột ngột. Sự chuyện đổi cũng c&oacute; thể gi&uacute;p bạn sử dụng để tạo ra h&igrave;nh ảnh của kh&ocirc;ng gian lớn bằng việc tạo chiều s&acirc;u cho khu vườn qua việc sử dụng c&aacute;c loại c&acirc;y nhỏ v&agrave;o ph&iacute;a sau c&aacute;c c&acirc;y cao hơn.</div>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p style="text-align: center;">&nbsp;</p>', 133, 0, 0, '', NULL, NULL, '0');
